import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { BehaviorSubject, Observable, of } from 'rxjs';
import { catchError, map, startWith } from 'rxjs/operators';
import { DataState } from './Enum/data-state.enum';
import { Status } from './Enum/status.enum';
import { AppState } from './Interface/app-state';
import { CustomResponse } from './Interface/custom-response';
import { Server } from './Interface/server';
import { ServerService } from './Service/server.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  appState$: Observable<AppState<CustomResponse>>;

  readonly DataState = DataState;
  readonly Status = Status
  private filterSubject = new BehaviorSubject<string>('');
  private dataSubject = new BehaviorSubject<CustomResponse>(null);
  filterStatus$ = this.filterSubject.asObservable();
  private isLoading = new BehaviorSubject<boolean>(false);
  isLoading$ = this.isLoading.asObservable();

  constructor(private serverService: ServerService) {  }

  ngOnInit(): void{
    this.appState$ = this.serverService.servers$
    .pipe(
      map(response => {
        this.dataSubject.next(response);
        return { dataState: DataState.LOADED_STATE, appData: {...response, data:{servers: response.data.servers.reverse()}} }
      }),
      startWith({ dataState: DataState.LOADING_STATE, appData: null }),
      catchError((error: string) => {
        return of({ dataState: DataState.ERROR_STATE, error: error })
      })
    );
  }

  pingServer(ipAddress: string): void{
    this.filterSubject.next(ipAddress);
    this.appState$ = this.serverService.ping$(ipAddress)
    .pipe(
      map(response => {
        const index = this.dataSubject.value.data.servers.findIndex(server => server.id === response.data.server.id);
        this.dataSubject.value.data.servers[index] = response.data.server;
        this.filterSubject.next('');
        return { dataState: DataState.LOADED_STATE, appData:  this.dataSubject.value  }
      }),
      startWith({ dataState: DataState.LOADED_STATE, appData: this.dataSubject.value }),
      catchError((error: string) => {
        this.filterSubject.next('');
        return of({ dataState: DataState.ERROR_STATE, error: error })
      })
    );
  }

  saveServer(serverForm: NgForm): void{
    this.isLoading.next(true);
    this.appState$ = this.serverService.save$(serverForm.value as Server)
    .pipe(
      map(response => {
        this.dataSubject.next(
          {...response, data: {servers: [response.data.server, ...this.dataSubject.value.data.servers]} }
        );
        document.getElementById('closeModal').click();
        this.isLoading.next(false);
        serverForm.resetForm( {status: this.Status.SERVER_DOWN} )
        return { dataState: DataState.LOADED_STATE, appData:  this.dataSubject.value  }
      }),
      startWith({ dataState: DataState.LOADED_STATE, appData: this.dataSubject.value }),
      catchError((error: string) => {
        this.isLoading.next(false);
        return of({ dataState: DataState.ERROR_STATE, error: error })
      })
    );
  }

  deleteServer(server: Server): void{
    this.appState$ = this.serverService.delete$(server.id)
    .pipe(
      map(response => {
         this.dataSubject.next(
            {...response, data: {servers: this.dataSubject.value.data.servers.filter(serverItem => serverItem.id !== server.id)}}
         );
        return { dataState: DataState.LOADED_STATE, appData:  this.dataSubject.value  }
      }),
      startWith({ dataState: DataState.LOADED_STATE, appData: this.dataSubject.value }),
      catchError((error: string) => {
        return of({ dataState: DataState.ERROR_STATE, error: error })
      })
    );
  }

  printReport(): void{
let dataType = 'application/vnd.ms-exce.sheet.macroEnabled.12';
let tableSelect = document.getElementById('servers');
let tableHtml = tableSelect.outerHTML.replace(/ /g, '%20')
let downloadLink = document.createElement('a');
document.body.appendChild(downloadLink);
downloadLink.href = 'data:' + dataType + ', ' + tableHtml;

downloadLink.download = 'server-report.xls';
downloadLink.click();
document.body.removeChild(downloadLink);

  }


 /*  filterServers(status: Status): void {
    this.appState$ = this.serverService.filter$(status, this.dataSubject.value)
      .pipe(
        map(response => {
          return { dataState: DataState.LOADED_STATE, appData: response };
        }),
        startWith({ dataState: DataState.LOADED_STATE, appData: this.dataSubject.value }),
        catchError((error: string) => {
          return of({ dataState: DataState.ERROR_STATE, error });
        })
      );
  } */

}

<div class="container-xl">
    <div class="table-responsive">
        <div class="table-wrapper">
            <div class="tabletitle">
                <div class="row">
                    <div class="col-sm-6">
                        <h2>Manage Servers</h2>
                    </div>
                    <div class="col-sm-6">
                        <button (click)="printReport()" type="button" class="btn btn-primary">Print Report</button>

                        <a href="#addEmployeeModal" class="btn btn-success" data-toggle="modal">
                            <i class="material-icons">&#xE147;</i>
                            <span>New Server</span>
                        </a>

                        <span>
                            <select name="status" ngModel="ALL"
                                class="btn btn-info" style="height: 32.91px;">
                                <option value="ALL">ALL</option>
                                <option value="SERVER_UP">SERVER UP</option>
                                <option value="SERVER_DOWN">SERVER DOWN</option>
                            </select>
                        </span>
                    </div>
                </div>
            </div>
            <br>
            <ng-container *ngIf="(appState$ | async) as appState" [ngSwitch]="appState.dataState">
                <ng-container *ngSwitchCase="DataState.LOADING_STATE">
                    <div class="col-md-12 single-note-item text-center">
                        <div class="spinner-border text-info" role="status"></div>
                    </div>
                </ng-container>
                <ng-container  *ngSwitchCase="DataState.LOADED_STATE">
                    <table class="table table-striper table-hover" id="servers">
                        <thead>
                            <tr>
                                <th>Image</th>
                                <th>IP Address</th>
                                <th>Name</th>
                                <th>Memory</th>
                                <th>Type</th>
                                <th>Status</th>
                                <th>Ping</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody *ngFor="let server of appState.appData.data.servers;">
                            <tr>
                                <td><img [src]="server.imageUrl" [alt]="server.name" width="40" height="40" alt=""></td>
                                <td>{{server.ipAddress}}</td>
                                <td>{{server.name}}</td>
                                <td>{{server.memory}}</td>
                                <td>{{server.type}}</td>
                                <td>
                                    <span class="badge" [ngClass]="[server.status === Status.SERVER_UP ? ' badge-success' : 'badge-danger']">
                                    {{server.status === Status.SERVER_UP ? 'SERVER UP' : 'SERVER DOWN'}}
                                </span>
                                </td>
                                <td>
                                    <a (click)="pingServer(server.ipAddress)" style="cursor:pointer">
                                        <i *ngIf="(filterStatus$ | async) === '' || (filterStatus$ | async) !== server.ipAddress" class="material-icons" title="Ping server">&#xe328;</i>
                                        <i *ngIf="(filterStatus$ | async) === server.ipAddress" class="fa fa-spinner fa-spin" style="font-size: 24px;"></i>
                                    </a>
                                </td>
                                <td>
                                    <a (click)="deleteServer(server)" class="delete" data-toggle="modal" style="cursor:pointer">
                                        <i class="material-icons" data-toggle="" title="Delete">&#xE872;</i>
                                    </a>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </ng-container>
                <ng-container  *ngSwitchCase="DataState.ERROR_STATE">
                    <div class="alert-danger">
                        {{appState.error}}
                    </div>
                </ng-container>

            </ng-container>
        </div>
    </div>
</div>


<div id="addEmployeeModal" class="modal fade">
  <div class="modal-dialog">
    <div class="modal-content">
      <form #serverForm="ngForm" (ngSubmit)="saveServer(serverForm)">
        <div class="modal-header">
          <h4 class="modal-title">Add Server</h4>
          <button type="button" class="close" data-dismiss="modal" aria-hidden="true"></button>
        </div>
        <div class="modal-body">
          <div class="form-group">
            <label for="Ip">IP</label>
            <input type="text" ngModel name="ipAddress" class="form-control" required>
          </div>
          <div class="form-group">
            <label for="Ip">Name</label>
            <input type="text" ngModel name="name" class="form-control" required>
          </div>
          <div class="row">
            <div class="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-6">
              <div class="form-group">
                <label for="">Memory</label>
                <input type="text" ngModel name="memory" class="form-control" required>
              </div>
            </div>
            <div class="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-6">
              <div class="form-group">
                <label for="">Type</label>
                <input type="text" ngModel name="type" class="form-control" required>
              </div>
            </div>
          </div>
          <div class="form-group">
            <label for="">Status</label>
            <select name="status" ngModel="SERVER_DOWN" class="form-control" required>
              <option value="SERVER_UP">SERVER UP</option>
              <option value="SERVER_UP">SERVER DOWN</option>
            </select>
          </div>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-warning" id="closeModal" data-dismiss="modal">
            CANCEL
          </button>
          <button type="submit" [disabled]="serverForm.invalid || (isLoading$ | async)" class="btn btn-success">
            <i *ngIf="isLoading$ | async" class="fas fa-spinner fa-spin"></i>
            <span *ngIf="isLoading$ | async" >Saving...</span>
            <span *ngIf="!(isLoading$ | async)" >Add</span>
          </button>
        </div>
      </form>
    </div>
  </div>

</div>
